import EventEmitter = require('events');
import {
  MESSAGE_TYPE,
  NEOX_EVENT,
  requestTargetEVM,
} from '../common/data_module_evm';
import { ERRORS, EVENT } from '../common/data_module_neo2';
import { checkConnectAndLogin, getIcon, sendMessage } from './common';
import { ethErrors } from 'eth-rpc-errors';
import { ChainType } from '../common/constants';

enum EventName {
  accountsChanged = 'accountsChanged',
  chainChanged = 'chainChanged',
  connect = 'connect',
  disconnect = 'disconnect',
}

class NEOLineEVMController extends EventEmitter {
  isNEOLine = true;
  chainId = '';
  networkVersion = '';
  constructor() {
    super();
    this.setMaxListeners(100);
  }
  /**
   * Submits an RPC request for the given method, with the given params.
   * Resolves with the result of the method call, or rejects on error.
   *
   * @param args - The RPC request arguments.
   * @param args.method - The RPC method name.
   * @param args.params - The parameters for the RPC method.
   * @returns A Promise that resolves with the result of the RPC method,
   * or rejects if an error is encountered.
   */
  async request(args: {
    method: string;
    params?: Array<unknown>;
  }): Promise<any> {
    if (!args || typeof args !== 'object' || Array.isArray(args)) {
      throw ethErrors.rpc.invalidRequest({
        message: 'Expected a single, non-array, object argument.',
        data: args,
      });
    }

    const { method, params } = args;

    if (typeof method !== 'string' || method.length === 0) {
      throw ethErrors.rpc.invalidRequest({
        message: "'args.method' must be a non-empty string.",
        data: args,
      });
    }

    if (
      params !== undefined &&
      !Array.isArray(params) &&
      (typeof params !== 'object' || params === null)
    ) {
      throw ethErrors.rpc.invalidRequest({
        message: "'args.params' must be an object or array if provided.",
        data: args,
      });
    }

    if (method === MESSAGE_TYPE.ETH_REQUEST_ACCOUNTS) {
      const isAuth = await checkConnectAndLogin(ChainType.NeoX);
      if (isAuth === true) {
        return sendMessage(requestTargetEVM.request, args);
      }
      throw ethErrors.provider.userRejectedRequest().serialize();
    }

    (args as any).hostInfo = {
      hostname: location.hostname,
      icon: getIcon(),
      origin: location.origin,
    };

    return sendMessage(requestTargetEVM.request, args);
  }
}

const provider = new Proxy(new NEOLineEVMController(), {
  get: (e, t) => e[t],
  deleteProperty: () => !0,
});

window.addEventListener('message', (e) => {
  const response = e.data;
  if (
    response.return === NEOX_EVENT.EVM_ACCOUNT_CHANGED &&
    Array.isArray(response.data)
  ) {
    provider.emit(EventName.accountsChanged, response.data);
  }
  if (response.return === NEOX_EVENT.EVM_NETWORK_CHANGED) {
    const chainId = response.data?.chainId;
    const hexChainId = '0x' + Number(chainId).toString(16);
    provider.chainId = hexChainId;
    provider.networkVersion = chainId.toString();
    provider.emit(EventName.chainChanged, hexChainId);
  }
  if (response.return === NEOX_EVENT.INIT_CHAIN_ID) {
    const chainId = response.data;
    const hexChainId = '0x' + Number(chainId).toString(16);
    provider.chainId = hexChainId;
    provider.networkVersion = chainId.toString();
  }
});

Reflect.set(window, 'NEOLineNeoX', provider);

function announceProvider() {
  const info: EIP6963ProviderInfo = {
    uuid: 'bb14cf73-2959-456f-a26e-f5d87e08013e',
    name: 'NeoLine',
    icon: 'data:image/svg+xml;charset=utf-8;base64,',
    rdns: 'io.neoline',
  };
  window.dispatchEvent(
    new CustomEvent('eip6963:announceProvider', {
      detail: Object.freeze({ info, provider }),
    })
  );
}

window.addEventListener(
  'eip6963:requestProvider',
  (event: EIP6963RequestProviderEvent) => {
    announceProvider();
  }
);

announceProvider();

/**
 * Represents the assets needed to display a wallet
 */
interface EIP6963ProviderInfo {
  uuid: string;
  name: string;
  icon: string;
  rdns: string;
}

// Request Event dispatched by a DApp
interface EIP6963RequestProviderEvent extends Event {
  type: 'eip6963:requestProvider';
}
